const INPUT: &'static str = "



";

fn main() {
  let mut x = 1;
  let mut y = 1;

  for line in INPUT.lines() {
    for command in line.chars() {
      match command {
        'U' => y -= 1,
        'D' => y += 1,
        'L' => x -= 1,
        'R' => x += 1,
        _ => unreachable!(),
      }

      x = if x < 0 { 0 } else if x > 2 { 2 } else { x };
      y = if y < 0 { 0 } else if y > 2 { 2 } else { y };
    }

    print!("{}", y * 3 + x + 1);
  }
  println!("");
}
